mapper.select:
SELECT ID, PW, EMAIL
  FROM member
 WHERE id = #{id}

SELECT ID, EMAIL, NAME
  FROM MEMBER
 WHERE EMAIL = #{email}

mapper.update:
UPDATE MEMBER 
   SET PW = #{pw}
 WHERE EMAIL = #{email}

UPDATE MEMBER
   SET PW = #{pw}
 WHERE EMAIL = #{email}

UPDATE MEMBER
   SET NAME = #{name}
      , birthday = #{birthday}
      , gender = #{gender}
      , phone = #{phone}
 WHERE M_NO = #{mNo} AND Id = #{id}

UPDATE MEMBER
   SET STATE = -1
 WHERE M_NO = #{mNo}
